package base64util

import (
	"fmt"
	"testing"

	"github.com/cpusoft/goutil/fileutil"
)

func TestASA(t *testing.T) {
	base := ``
	b, e := DecodeBase64(base)
	fmt.Println(len(b), e)

	file := "1.asa"
	e = fileutil.WriteBytesToFile(file, b)
	fmt.Println(e)
}
